export default App;

import React, { createContext, useContext, useState, useMemo } from 'react';
import type { IDataProvider } from './types';
import { StaticProvider } from './StaticProvider';
import { LiveProvider } from './LiveProvider';

interface DataProviderContextType {
  provider: IDataProvider;
  mode: 'demo' | 'live';
  setMode: (mode: 'demo' | 'live') => void;
  configureLive: (url: string, apiKey: string) => void;
}

const DataProviderContext = createContext<DataProviderContextType | null>(null);

export const DataProviderProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [mode, setMode] = useState<'demo' | 'live'>('demo');
  const [liveConfig, setLiveConfig] = useState({ url: 'http://localhost:8000', apiKey: '' });

  const provider = useMemo(() => {
    if (mode === 'live') {
      return new LiveProvider(liveConfig.url, liveConfig.apiKey);
    }
    return new StaticProvider();
  }, [mode, liveConfig]);

  const configureLive = (url: string, apiKey: string) => {
    setLiveConfig({ url, apiKey });
  };

  return (
    <DataProviderContext.Provider value={{ provider, mode, setMode, configureLive }}>
      {children}
    </DataProviderContext.Provider>
  );
};

export const useDataProvider = () => {
  const context = useContext(DataProviderContext);
  if (!context) {
    throw new Error('useDataProvider must be used within a DataProviderProvider');
  }
  return context;
};

import type { IDataProvider, MarketData, AnalystRating, CorporateEarning, SocialSentiment } from './types';
import axios, { type AxiosInstance } from 'axios';

export class LiveProvider implements IDataProvider {
  private client: AxiosInstance;

  constructor(baseURL: string = 'http://localhost:8000', apiKey: string = '') {
    this.client = axios.create({
      baseURL,
      headers: {
        'X-API-Key': apiKey,
      },
    });
  }

  async getMarketData(ticker: string): Promise<MarketData[]> {
    try {
      const response = await this.client.get(`/api/v1/market-data/${ticker}`);
      return response.data;
    } catch (error) {
      console.error('Failed to fetch market data from backend', error);
      return [];
    }
  }

  async getAnalystRatings(ticker: string): Promise<AnalystRating[]> {
    try {
      const response = await this.client.get(`/api/v1/analyst-ratings/${ticker}`);
      return response.data;
    } catch (error) {
      console.error('Failed to fetch analyst ratings from backend', error);
      return [];
    }
  }

  async getCorporateEarnings(ticker: string): Promise<CorporateEarning[]> {
    try {
      const response = await this.client.get(`/api/v1/corporate-earnings/${ticker}`);
      return response.data;
    } catch (error) {
      console.error('Failed to fetch corporate earnings from backend', error);
      return [];
    }
  }

  async getSocialSentiment(ticker: string): Promise<SocialSentiment[]> {
    try {
        // Adjust endpoint as necessary based on actual backend implementation
      const response = await this.client.get(`/api/v1/sentiment/${ticker}`);
      return response.data;
    } catch (error) {
      console.error('Failed to fetch sentiment data from backend', error);
      return [];
    }
  }
}

import type { IDataProvider, MarketData, AnalystRating, CorporateEarning, SocialSentiment } from './types';
import axios from 'axios';

export class StaticProvider implements IDataProvider {
  async getMarketData(ticker: string): Promise<MarketData[]> {
    const response = await axios.get('/data/market_data_1min.json');
    const data: MarketData[] = response.data;
    return data.filter(item => item.Ticker === ticker);
  }

  async getAnalystRatings(ticker: string): Promise<AnalystRating[]> {
    const response = await axios.get('/data/analyst_ratings.json');
    const data: AnalystRating[] = response.data;
    return data.filter(item => item.Ticker === ticker);
  }

  async getCorporateEarnings(ticker: string): Promise<CorporateEarning[]> {
    const response = await axios.get('/data/corporate_earnings.json');
    const data: CorporateEarning[] = response.data;
    return data.filter(item => item.Ticker === ticker);
  }

  async getSocialSentiment(ticker: string): Promise<SocialSentiment[]> {
    const response = await axios.get('/data/social_hourly_features.json');
    const data: SocialSentiment[] = response.data;
    return data.filter(item => item.Ticker === ticker);
  }
}

