import React, { useMemo } from 'react';
import {
  ScatterChart,
  Scatter,
  XAxis,
  YAxis,
  ZAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer
} from 'recharts';

// Static topics for simulation
const TOPICS = ['Earnings', 'Geopolitics', 'Mergers', 'Regulations', 'Product Launch'];
const COLORS = ['#8884d8', '#82ca9d', '#ffc658', '#ff7300', '#0088fe'];

export const ClusterScatterPlot: React.FC = () => {
  const data = useMemo(() => {
    // Generate random clusters
    const clusters = [];
    for (let i = 0; i < 50; i++) {
        const topicIndex = Math.floor(Math.random() * TOPICS.length);
        const topic = TOPICS[topicIndex];
        // Create clusters by biasing coordinates based on topic
        const xBias = topicIndex * 100;
        const yBias = (topicIndex % 2) * 100;

        clusters.push({
            x: xBias + Math.random() * 80,
            y: yBias + Math.random() * 80,
            z: Math.random() * 100, // Impact score
            topic
        });
    }
    return clusters;
  }, []);

  return (
    <div className="bg-gray-900 border border-gray-800 p-4 rounded-lg flex flex-col h-[400px]">
      <h2 className="text-xl font-semibold text-green-500 mb-4">
        News Clusters (Semantic Grouping)
      </h2>
      <div className="flex-grow">
        <ResponsiveContainer width="100%" height="100%">
          <ScatterChart>
            <CartesianGrid strokeDasharray="3 3" stroke="#444" />
            <XAxis type="number" dataKey="x" name="Semantic Dim 1" stroke="#888" />
            <YAxis type="number" dataKey="y" name="Semantic Dim 2" stroke="#888" />
            <ZAxis type="number" dataKey="z" range={[50, 400]} name="Impact" />
            <Tooltip
                cursor={{ strokeDasharray: '3 3' }}
                content={({ active, payload }) => {
                    if (active && payload && payload.length) {
                        const data = payload[0].payload;
                        return (
                            <div className="bg-gray-900 p-3 border border-gray-700 rounded shadow-lg text-sm">
                                <p className="text-white font-bold">{data.topic}</p>
                                <p className="text-gray-400">Impact: {data.z.toFixed(0)}</p>
                            </div>
                        );
                    }
                    return null;
                }}
            />
            <Legend />
            {TOPICS.map((topic, index) => (
                <Scatter
                    key={topic}
                    name={topic}
                    data={data.filter(d => d.topic === topic)}
                    fill={COLORS[index % COLORS.length]}
                />
            ))}
          </ScatterChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

import React, { useMemo } from 'react';
import {
  ComposedChart,
  Line,
  Scatter,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer
} from 'recharts';
import type { MarketData } from '../../api/types';

interface PriceAnomalyChartProps {
  data: MarketData[];
  ticker: string;
}

export const PriceAnomalyChart: React.FC<PriceAnomalyChartProps> = ({ data, ticker }) => {
  const processedData = useMemo(() => {
    if (!data.length) return [];

    const prices = data.map(d => d.Close);
    const mean = prices.reduce((a, b) => a + b, 0) / prices.length;
    const stdDev = Math.sqrt(prices.reduce((a, b) => a + Math.pow(b - mean, 2), 0) / prices.length);

    return data.map(d => ({
      ...d,
      time: new Date(d.Timestamp).toLocaleTimeString(),
      anomaly: Math.abs(d.Close - mean) > 2 * stdDev ? d.Close : null
    }));
  }, [data]);

  return (
    <div className="bg-gray-900 border border-gray-800 p-4 rounded-lg flex flex-col h-[400px]">
      <h2 className="text-xl font-semibold text-green-500 mb-4">
        Early Warning Indicator ({ticker})
      </h2>
      <div className="flex-grow">
        <ResponsiveContainer width="100%" height="100%">
          <ComposedChart data={processedData}>
            <CartesianGrid strokeDasharray="3 3" stroke="#444" />
            <XAxis dataKey="time" stroke="#888" />
            <YAxis domain={['auto', 'auto']} stroke="#888" />
            <Tooltip
                contentStyle={{ backgroundColor: '#1e1e1e', border: '1px solid #444' }}
                itemStyle={{ color: '#fff' }}
            />
            <Legend />
            <Line
              type="monotone"
              dataKey="Close"
              stroke="#f97316"
              dot={false}
              strokeWidth={2}
              name="Price"
            />
            <Scatter name="Anomaly" dataKey="anomaly" fill="red" shape="circle" />
          </ComposedChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

import React, { useMemo } from 'react';
import {
  ComposedChart,
  Line,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer
} from 'recharts';
import type { MarketData, SocialSentiment } from '../../api/types';

interface SentimentPriceChartProps {
  marketData: MarketData[];
  sentimentData: SocialSentiment[];
  ticker: string;
}

export const SentimentPriceChart: React.FC<SentimentPriceChartProps> = ({ marketData, sentimentData, ticker }) => {
  const chartData = useMemo(() => {
    // Merge data by roughly matching timestamps or just using index if data is aligned.
    // For synthetic data, timestamps might not match exactly, so we'll try to align by closest hour or just take a subset.
    // Assuming 1-min market data and hourly sentiment. We can map market data to hourly average or just plot them on same time axis.

    // Simplification: We will just take the market data and try to find a matching sentiment record (or nearest).
    // Or better, let's just plot them independently if timestamps differ significantly.
    // But Recharts wants a single data array.

    // Let's index sentiment by timestamp (hour)
    const sentimentMap = new Map(sentimentData.map(s => {
        const timeKey = new Date(s.Timestamp).toISOString().substring(0, 13); // yyyy-mm-ddThh
        return [timeKey, s];
    }));

    // Downsample market data to hourly to match sentiment or just plot all market data and sparsely populate sentiment
    // Let's filter market data to hourly for clearer visualization
    const hourlyMarketData = marketData.filter((_, i) => i % 60 === 0); // Approx hourly if data is 1 min

    return hourlyMarketData.map(d => {
        const timeKey = new Date(d.Timestamp).toISOString().substring(0, 13);
        const sentiment = sentimentMap.get(timeKey);
        return {
            time: new Date(d.Timestamp).toLocaleString(),
            price: d.Close,
            sentimentVolume: sentiment ? sentiment.Social_Media_Volume : 0,
            sentimentDivergence: sentiment ? sentiment.Sentiment_Divergence : 0
        };
    });
  }, [marketData, sentimentData]);

  return (
    <div className="bg-gray-900 border border-gray-800 p-4 rounded-lg flex flex-col h-[400px]">
      <h2 className="text-xl font-semibold text-green-500 mb-4">
        Sentiment Correlation ({ticker})
      </h2>
      <div className="flex-grow">
        <ResponsiveContainer width="100%" height="100%">
          <ComposedChart data={chartData}>
            <CartesianGrid strokeDasharray="3 3" stroke="#444" />
            <XAxis dataKey="time" stroke="#888" />
            <YAxis yAxisId="left" stroke="#f97316" label={{ value: 'Price', angle: -90, position: 'insideLeft' }} />
            <YAxis yAxisId="right" orientation="right" stroke="#82ca9d" label={{ value: 'Sentiment Volume', angle: 90, position: 'insideRight' }} />
            <Tooltip
                contentStyle={{ backgroundColor: '#1e1e1e', border: '1px solid #444' }}
                itemStyle={{ color: '#fff' }}
            />
            <Legend />
            <Line yAxisId="left" type="monotone" dataKey="price" stroke="#f97316" dot={false} />
            <Bar yAxisId="right" dataKey="sentimentVolume" barSize={20} fill="#82ca9d" opacity={0.5} />
          </ComposedChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';

const Node = ({ name, color, isActive }: { name: string, color: string, isActive: boolean }) => (
  <div
    className={`w-24 h-24 flex flex-col justify-center items-center rounded-full border-4 bg-gray-900 z-10 transition-colors duration-500`}
    style={{ borderColor: isActive ? color : '#374151' }}
  >
    <span className="text-xs font-bold text-gray-200">{name}</span>
  </div>
);

const FederatedLearningVisualizer: React.FC = () => {
    const [cycle, setCycle] = useState(0);

    useEffect(() => {
        const interval = setInterval(() => {
            setCycle(c => (c + 1) % 4); // 0: Idle, 1: Train, 2: Upload, 3: Aggregation
        }, 2000);
        return () => clearInterval(interval);
    }, []);

    const clients = [
        { id: 1, name: 'Client A', color: '#f97316' }, // Orange
        { id: 2, name: 'Client B', color: '#3b82f6' }, // Blue
        { id: 3, name: 'Client C', color: '#22c55e' }, // Green
    ];

    return (
        <div className="p-8 h-full flex flex-col items-center bg-gray-950">
            <h2 className="text-2xl font-bold text-white mb-2">Federated Learning Simulation</h2>
            <p className="text-gray-400 mb-8">
                Status: <span className="text-green-400">{['Idle', 'Training Local Models', 'Sending Updates', 'Global Aggregation'][cycle]}</span>
            </p>

            <div className="relative w-full max-w-2xl h-[500px] flex justify-center">

                {/* Connecting Lines (Static SVG) */}
                <svg className="absolute inset-0 w-full h-full z-0 pointer-events-none">
                    <line x1="50%" y1="120" x2="20%" y2="400" stroke="#374151" strokeWidth="2" />
                    <line x1="50%" y1="120" x2="50%" y2="400" stroke="#374151" strokeWidth="2" />
                    <line x1="50%" y1="120" x2="80%" y2="400" stroke="#374151" strokeWidth="2" />
                </svg>

                {/* Global Model */}
                <div className="absolute top-10">
                    <Node name="Global Model" color="#ffffff" isActive={cycle === 3} />
                </div>

                {/* Clients */}
                <div className="absolute bottom-10 w-full flex justify-between px-10 md:px-20">
                    {clients.map((client, index) => (
                        <div key={client.id} className="relative">
                            <Node name={client.name} color={client.color} isActive={cycle === 1} />

                            {/* Packet Animation (Upload) */}
                            {cycle === 2 && (
                                <motion.div
                                    initial={{ opacity: 1, y: 0 }}
                                    animate={{
                                        y: -280, // Distance to global model
                                        x: index === 0 ? 100 : index === 2 ? -100 : 0, // Convergence towards center
                                        opacity: 0
                                    }}
                                    transition={{ duration: 1.5 }}
                                    className="absolute top-0 left-1/2 w-3 h-3 rounded-full transform -translate-x-1/2"
                                    style={{ backgroundColor: client.color }}
                                />
                            )}

                            {/* Download Animation (Aggregation) */}
                            {cycle === 3 && (
                                <motion.div
                                    initial={{
                                        opacity: 0,
                                        y: -280,
                                        x: index === 0 ? 100 : index === 2 ? -100 : 0,
                                    }}
                                    animate={{ y: 0, x: 0, opacity: 1 }}
                                    transition={{ duration: 1.5 }}
                                    className="absolute top-0 left-1/2 w-3 h-3 bg-white rounded-full transform -translate-x-1/2"
                                />
                            )}
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default FederatedLearningVisualizer;

