import * as fs from 'fs';
import * as path from 'path';

const SOURCE_DIR = 'foundational_dataset_synthetic';
const DEST_DIR = 'frontend/public/data';

if (!fs.existsSync(DEST_DIR)) {
    fs.mkdirSync(DEST_DIR, { recursive: true });
}

const parseValue = (val: string) => {
    // Remove quotes if present
    if (val.startsWith('"') && val.endsWith('"')) {
        val = val.slice(1, -1);
    }
    // Try parse number
    // careful with empty strings
    if (val === '') return val;

    if (!isNaN(Number(val))) {
        return Number(val);
    }
    return val;
};

const parseLine = (line: string, headers: string[]) => {
    let row: any = {};
    let currentVal = '';
    let insideQuotes = false;
    let colIndex = 0;

    for (let i = 0; i < line.length; i++) {
        const char = line[i];
        if (char === '"') {
            insideQuotes = !insideQuotes;
        } else if (char === ',' && !insideQuotes) {
            if (colIndex < headers.length) {
                row[headers[colIndex]] = parseValue(currentVal.trim());
            }
            colIndex++;
            currentVal = '';
        } else {
            currentVal += char;
        }
    }
    // Last value
    if (colIndex < headers.length) {
        row[headers[colIndex]] = parseValue(currentVal.trim());
    }
    return row;
};

const csvToJson = (filename: string) => {
    const filePath = path.join(SOURCE_DIR, filename);
    if (!fs.existsSync(filePath)) {
        console.warn(`File not found: ${filePath}`);
        return;
    }

    const fileContent = fs.readFileSync(filePath, 'utf-8');
    const lines = fileContent.split('\n');

    // Filter out comments and empty lines
    const validLines = lines.filter(line => line.trim() !== '' && !line.trim().startsWith('#'));

    if (validLines.length === 0) return;

    const headers = validLines[0].split(',').map(h => h.trim());
    const data = validLines.slice(1).map(line => parseLine(line, headers));

    const outputFilename = filename.replace('.csv', '.json');
    fs.writeFileSync(path.join(DEST_DIR, outputFilename), JSON.stringify(data, null, 2));
    console.log(`Converted ${filename} to ${outputFilename}`);
};

const files = [
    'market_data_1min.csv',
    'analyst_ratings.csv',
    'corporate_earnings.csv',
    'social_hourly_features.csv'
];

files.forEach(csvToJson);

import os
import json

REPO_ROOT = "."
OUTPUT_FILE = "frontend/public/repo_map.json"

EXCLUDE_DIRS = {
    ".git", "__pycache__", "node_modules", "dist", "venv", "env", ".idea", ".vscode", "frontend/dist"
}

EXCLUDE_EXTENSIONS = {
    ".pyc", ".ico", ".png", ".jpg", ".jpeg", ".svg", ".parquet", ".zip", ".tar", ".gz", ".woff", ".woff2", ".ttf", ".eot"
}

TEXT_EXTENSIONS = {
    ".py", ".ts", ".tsx", ".js", ".jsx", ".md", ".json", ".txt", ".sql", ".yml", ".yaml", ".css", ".html", ".sh", ".gitignore"
}

MAX_FILE_SIZE = 100 * 1024  # 100 KB

def get_file_type(filename):
    if filename.startswith("."):
        return "config"
    ext = os.path.splitext(filename)[1]
    if ext in TEXT_EXTENSIONS:
        return "text"
    return "binary"

def build_tree(path):
    name = os.path.basename(path)
    if name == "":
        name = "root"

    node = {
        "name": name,
        "path": path,
        "type": "folder",
        "children": []
    }

    try:
        entries = sorted(os.listdir(path))
    except PermissionError:
        return None

    for entry in entries:
        full_path = os.path.join(path, entry)

        if os.path.isdir(full_path):
            if entry in EXCLUDE_DIRS:
                continue
            child = build_tree(full_path)
            if child:
                node["children"].append(child)
        else:
            ext = os.path.splitext(entry)[1]
            if ext in EXCLUDE_EXTENSIONS:
                continue

            size = os.path.getsize(full_path)
            file_node = {
                "name": entry,
                "path": full_path,
                "type": "file",
                "size": size,
                "file_type": get_file_type(entry)
            }

            if size < MAX_FILE_SIZE and get_file_type(entry) in ["text", "config"]:
                try:
                    with open(full_path, "r", encoding="utf-8") as f:
                        file_node["content"] = f.read()
                except Exception as e:
                    file_node["content"] = f"Error reading file: {str(e)}"

            node["children"].append(file_node)

    return node

def main():
    print(f"Generating repo map from {REPO_ROOT}...")

    # Ensure output directory exists
    os.makedirs(os.path.dirname(OUTPUT_FILE), exist_ok=True)

    tree = build_tree(REPO_ROOT)

    with open(OUTPUT_FILE, "w", encoding="utf-8") as f:
        json.dump(tree, f, indent=2)

    print(f"Repo map saved to {OUTPUT_FILE}")

if __name__ == "__main__":
    main()

// Basic Interfaces for the Semantic Narrative Library

export interface NarrativeEntity {
  id: string;
  name: string;
  description?: string;
  type: string; // e.g., "Industry", "Company", "MacroIndicator"
  attributes?: Record<string, any>; // Flexible key-value store
  tags?: string[]; // For categorization and search
}

export interface Driver {
  id: string;
  name: string;
  description: string;
  impactPotential?: 'High' | 'Medium' | 'Low' | string; // Allow string for custom values
  // Example: How this driver typically influences things
  influencePattern?: string;
  // Entities this driver is commonly associated with
  relatedEntityTypes?: string[];
  tags?: string[];
}

export interface Relationship {
  id: string;
  sourceId: string; // ID of the source NarrativeEntity or Driver
  targetId: string; // ID of the target NarrativeEntity or Driver
  type: string; // e.g., "influences", "owns", "competes_with", "is_part_of", "has_driver"
  strength?: number; // e.g., 0.0 to 1.0
  direction?: 'unidirectional' | 'bidirectional';
  explanationTemplate?: string; // e.g., "{sourceName} {type} {targetName}"
  attributes?: Record<string, any>;
  tags?: string[];
}

export interface SemanticLink {
  id: string;
  // ID of the element being linked (e.g., a specific Driver instance affecting a Company, or a generated narrative)
  narrativeElementId: string;
  metricObserved: string; // e.g., "StockPrice", "AnalystRating", "SentimentScore"
  // Could be a specific value, a range, or a qualitative observation
  observedValue?: string | number | [number, number];
  observationTime?: Date;
  sourceOfObservation?: string; // Where this link was derived from
  confidence?: number; // e.g., 0.0 to 1.0
  explanation: string; // Why this link/observation is relevant
  tags?: string[];
}

// Example of a more specific entity type
export interface Company extends NarrativeEntity {
  type: 'Company';
  tickerSymbol?: string;
  industryId?: string;
  country?: string;
}

export interface Industry extends NarrativeEntity {
